ModelAccountCustomField.getCustomField.getCustomField=SELECT cf.custom_field_id, cf.type, cf.value, cf.validation, cf.location, cf.status, cf.sort_order, cfd.custom_field_id as custom_field_id0, cfd.language_id, cfd.name FROM "oc_custom_field" cf LEFT JOIN "oc_custom_field_description" cfd ON( cf.custom_field_id = cfd.custom_field_id ) WHERE cf.status = '1' AND cf.custom_field_id = 'param0' AND cfd.language_id = 'param1'  
#END
ModelAccountCustomField.getCustomFields.getCustomFieldQuery=SELECT cfcg.custom_field_id, cfcg.customer_group_id, cfcg.required, cf.custom_field_id as custom_field_id0, cf.type, cf.value, cf.validation, cf.location, cf.status, cf.sort_order, cfd.custom_field_id as custom_field_id01, cfd.language_id, cfd.name FROM "oc_custom_field_customer_group" cfcg LEFT JOIN "oc_custom_field" cf ON( cfcg.custom_field_id = cf.custom_field_id ) LEFT JOIN "oc_custom_field_description" cfd ON( cf.custom_field_id = cfd.custom_field_id ) WHERE cf.status = '1' AND cfd.language_id = 'param0' AND cfcg.customer_group_id = 'param1' ORDER BY cf.sort_order ASC 
#END
